mapper.select:
	SELECT 
      cer_certificado_detalle_id , 
      cer_certificado_id , 
      cer_nota_pedido_detalle_id , 
      subpackno , 
      rango , 
      num_orden , 
      nivel_estructura , 
      indicador_item_adici , 
      cta_contable , 
      precio_unitario , 
      descuento , 
      cantidad_total , 
      cantidad_pendiente , 
      cantidad_aprobada , 
      total_linea , 
      total_linea_ajustado , 
      asign_servicio , 
      auxiliar , 
      mtr_cuenta_imputacion_id , 
      mtr_cuenta_mayor_id 
	FROM
	cer_certificado_detalle
	WHERE 1 = 1

		AND cer_certificado_detalle_id = #{id, jdbcType=INTEGER}


		AND cer_certificado_id = #{cerCertificado.id, jdbcType=INTEGER}


		AND cer_nota_pedido_detalle_id = #{cerNotaPedidoDetalle.id, jdbcType=INTEGER}


		AND subpackno = #{subpackno}


		AND rango = #{rango}


		AND num_orden = #{numOrden}


		AND nivel_estructura = #{nivelEstructura}


		AND indicador_item_adici = #{indicadorItemAdici}


		AND cta_contable = #{ctaContable}


		AND precio_unitario = #{precioUnitario, jdbcType=NUMERIC}


		AND descuento = #{descuento, jdbcType=NUMERIC}


		AND cantidad_total = #{cantidadTotal, jdbcType=NUMERIC}


		AND cantidad_pendiente = #{cantidadPendiente, jdbcType=NUMERIC}


		AND cantidad_aprobada = #{cantidadAprobada, jdbcType=NUMERIC}


		AND total_linea = #{totalLinea, jdbcType=NUMERIC}


		AND total_linea_ajustado = #{totalLineaAjustado, jdbcType=NUMERIC}


		AND asign_servicio = #{asignServicio}


		AND auxiliar = #{auxiliar}


		AND mtr_cuenta_imputacion_id = #{mtrCuentaImputacion.id, jdbcType=INTEGER}


		AND mtr_cuenta_mayor_id = #{mtrCuentaMayor.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO cer_certificado_detalle( cer_certificado_detalle_id , cer_certificado_id , cer_nota_pedido_detalle_id , subpackno , rango , num_orden , nivel_estructura , indicador_item_adici , cta_contable , precio_unitario , descuento , cantidad_total , cantidad_pendiente , cantidad_aprobada , total_linea , total_linea_ajustado , asign_servicio , auxiliar , mtr_cuenta_imputacion_id , mtr_cuenta_mayor_id ) (
SELECT #{element.id} , #{element.cerCertificado.id} , #{element.cerNotaPedidoDetalle.id} , #{element.subpackno} , #{element.rango} , #{element.numOrden} , #{element.nivelEstructura} , #{element.indicadorItemAdici} , #{element.ctaContable} , #{element.precioUnitario} , #{element.descuento} , #{element.cantidadTotal} , #{element.cantidadPendiente} , #{element.cantidadAprobada} , #{element.totalLinea} , #{element.totalLineaAjustado} , #{element.asignServicio} , #{element.auxiliar} , #{element.mtrCuentaImputacion.id} , #{element.mtrCuentaMayor.id} from dual
)

mapper.delete:
