mapper.select:
	SELECT 
      fac_aprobador_id , 
      fac_factura_id , 
      mtr_aprobador_id , 
      estado 
	FROM
	fac_aprobador
	WHERE 1 = 1

		AND fac_aprobador_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id, jdbcType=INTEGER}


		AND mtr_aprobador_id = #{mtrAprobador.id, jdbcType=INTEGER}


		AND estado = #{estado}

mapper.insert:
INSERT INTO fac_aprobador( fac_aprobador_id , fac_factura_id , mtr_aprobador_id , estado ) (
SELECT #{element.id} , #{element.facFactura.id} , #{element.mtrAprobador.id} , #{element.estado} from dual
)

mapper.delete:
    DELETE FROM fac_aprobador
	WHERE 1 = 1

		AND fac_aprobador_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id}


		AND mtr_aprobador_id = #{mtrAprobador.id}


		AND estado = #{estado}
