mapper.select:
	SELECT 
      fac_contacto_id , 
      usuario , 
      contacto_nombres , 
      contacto_apellidos , 
      email , 
      tipo_contacto , 
      activo , 
      estado , 
      mtr_sector_id , 
      codigo_idp 
	FROM
	fac_contacto
	WHERE 1 = 1

		AND fac_contacto_id = #{id, jdbcType=INTEGER}


		AND usuario = #{usuario}


		AND contacto_nombres = #{contactoNombres}


		AND contacto_apellidos = #{contactoApellidos}


		AND email = #{email}


		AND tipo_contacto = #{tipoContacto}


		AND activo = #{activo}


		AND estado = #{estado}


		AND mtr_sector_id = #{mtrSector.id, jdbcType=INTEGER}


		AND codigo_idp = #{codigoIdp}

mapper.insert:
INSERT INTO fac_contacto( fac_contacto_id , usuario , contacto_nombres , contacto_apellidos , email , tipo_contacto , activo , estado , mtr_sector_id , codigo_idp ) (
SELECT #{element.id} , #{element.usuario} , #{element.contactoNombres} , #{element.contactoApellidos} , #{element.email} , #{element.tipoContacto} , #{element.activo} , #{element.estado} , #{element.mtrSector.id} , #{element.codigoIdp} from dual
)

mapper.delete:
    DELETE FROM fac_contacto
	WHERE 1 = 1

		AND fac_contacto_id = #{id, jdbcType=INTEGER}


		AND usuario = #{usuario}


		AND contacto_nombres = #{contactoNombres}


		AND contacto_apellidos = #{contactoApellidos}


		AND email = #{email}


		AND tipo_contacto = #{tipoContacto}


		AND activo = #{activo}


		AND estado = #{estado}


		AND mtr_sector_id = #{mtrSector.id}


		AND codigo_idp = #{codigoIdp}
