mapper.select:
	SELECT 
      MAREA_EVENTO_ID , 
      MTR_TIPO_EVENTO_ID , 
      FECHA_EVENTO , 
      HORA_VENTO , 
      MTR_ZONA_PESCA_ID , 
      MTR_ESTADO_ID , 
      STOCK 
	FROM
	MAREA_EVENTO
	WHERE 1 = 1

		AND MAREA_EVENTO_ID = #{id, jdbcType=INTEGER}


		AND MTR_TIPO_EVENTO_ID = #{mtrTipoEvento.id, jdbcType=INTEGER}


		AND FECHA_EVENTO = #{fechaEvento, jdbcType=DATE}


		AND HORA_VENTO = #{horaVento}


		AND MTR_ZONA_PESCA_ID = #{mtrZonaPesca.id, jdbcType=INTEGER}


		AND MTR_ESTADO_ID = #{mtrEstado.id, jdbcType=INTEGER}


		AND STOCK = #{stock, jdbcType=NUMERIC}

mapper.insert:
INSERT INTO MAREA_EVENTO( MAREA_EVENTO_ID , MTR_TIPO_EVENTO_ID , FECHA_EVENTO , HORA_VENTO , MTR_ZONA_PESCA_ID , MTR_ESTADO_ID , STOCK ) (
SELECT #{element.id} , #{element.mtrTipoEvento.id} , #{element.fechaEvento} , #{element.horaVento} , #{element.mtrZonaPesca.id} , #{element.mtrEstado.id} , #{element.stock} from dual
)

mapper.delete:
    DELETE FROM MAREA_EVENTO
	WHERE 1 = 1

		AND MAREA_EVENTO_ID = #{id, jdbcType=INTEGER}


		AND MTR_TIPO_EVENTO_ID = #{mtrTipoEvento.id}


		AND FECHA_EVENTO = #{fechaEvento, jdbcType=DATE}


		AND HORA_VENTO = #{horaVento}


		AND MTR_ZONA_PESCA_ID = #{mtrZonaPesca.id}


		AND MTR_ESTADO_ID = #{mtrEstado.id}


		AND STOCK = #{stock, jdbcType=NUMERIC}
