mapper.select:
	SELECT 
      MAREA_PESCA_ID , 
      NUMERO_MAREA , 
      MTR_EMBARCACION_ID , 
      MTR_HOROMETRO_ID , 
      VALOR_HOROMETRO , 
      MTR_TIPO_EVENTO_ID 
	FROM
	MAREA_PESCA
	WHERE 1 = 1

		AND MAREA_PESCA_ID = #{id, jdbcType=INTEGER}


		AND NUMERO_MAREA = #{numeroMarea}


		AND MTR_EMBARCACION_ID = #{mtrEmbarcacion.id, jdbcType=INTEGER}


		AND MTR_HOROMETRO_ID = #{mtrHorometro.id, jdbcType=INTEGER}


		AND VALOR_HOROMETRO = #{valorHorometro, jdbcType=NUMERIC}


		AND MTR_TIPO_EVENTO_ID = #{mtrTipoEvento.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO MAREA_PESCA( MAREA_PESCA_ID , NUMERO_MAREA , MTR_EMBARCACION_ID , MTR_HOROMETRO_ID , VALOR_HOROMETRO , MTR_TIPO_EVENTO_ID ) (
SELECT #{element.id} , #{element.numeroMarea} , #{element.mtrEmbarcacion.id} , #{element.mtrHorometro.id} , #{element.valorHorometro} , #{element.mtrTipoEvento.id} from dual
)

mapper.delete:
    DELETE FROM MAREA_PESCA
	WHERE 1 = 1

		AND MAREA_PESCA_ID = #{id, jdbcType=INTEGER}


		AND NUMERO_MAREA = #{numeroMarea}


		AND MTR_EMBARCACION_ID = #{mtrEmbarcacion.id}


		AND MTR_HOROMETRO_ID = #{mtrHorometro.id}


		AND VALOR_HOROMETRO = #{valorHorometro, jdbcType=NUMERIC}


		AND MTR_TIPO_EVENTO_ID = #{mtrTipoEvento.id}
