mapper.select:
	SELECT 
      mtr_cuenta_mayor_id , 
      mtr_tipo_imputacion_id , 
      codigo_cuenta_mayor , 
      descripcion , 
      mtr_sociedad_id 
	FROM
	mtr_cuenta_mayor
	WHERE 1 = 1

		AND mtr_cuenta_mayor_id = #{id, jdbcType=INTEGER}


		AND mtr_tipo_imputacion_id = #{mtrTipoImputacion.id, jdbcType=INTEGER}


		AND codigo_cuenta_mayor = #{codigoCuentaMayor}


		AND descripcion = #{descripcion}


		AND mtr_sociedad_id = #{mtrSociedad.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO mtr_cuenta_mayor( mtr_cuenta_mayor_id , mtr_tipo_imputacion_id , codigo_cuenta_mayor , descripcion , mtr_sociedad_id ) (
SELECT #{element.id} , #{element.mtrTipoImputacion.id} , #{element.codigoCuentaMayor} , #{element.descripcion} , #{element.mtrSociedad.id} from dual
)

mapper.delete:
    DELETE FROM mtr_cuenta_mayor
	WHERE 1 = 1

		AND mtr_cuenta_mayor_id = #{id, jdbcType=INTEGER}


		AND mtr_tipo_imputacion_id = #{mtrTipoImputacion.id}


		AND codigo_cuenta_mayor = #{codigoCuentaMayor}


		AND descripcion = #{descripcion}


		AND mtr_sociedad_id = #{mtrSociedad.id}
