mapper.select:
	SELECT 
      MTR_TIPO_PESCA_ID , 
      NOMBRE , 
      INUBC 
	FROM
	MTR_TIPO_PESCA
	WHERE 1 = 1

		AND MTR_TIPO_PESCA_ID = #{id, jdbcType=INTEGER}


		AND NOMBRE = #{nombre}


		AND INUBC = #{inubc}

mapper.insert:
INSERT INTO MTR_TIPO_PESCA( MTR_TIPO_PESCA_ID , NOMBRE , INUBC ) (
SELECT #{element.id} , #{element.nombre} , #{element.inubc} from dual
)

mapper.delete:
    DELETE FROM MTR_TIPO_PESCA
	WHERE 1 = 1

		AND MTR_TIPO_PESCA_ID = #{id, jdbcType=INTEGER}


		AND NOMBRE = #{nombre}


		AND INUBC = #{inubc}
